import React from "react";
import styles from "./RandomNewsFeed.module.css";
import NavBar from "./NavBar";

function RandomNewsFeed() {
  let [feed, setFeed] = useState([]);
  let [error, setError] = useState(null);

  async function fetchedNewsHandler() {
    setFeed([]);
    let endpoint =
      "https://gnews.io/api/v4/top-headlines?token=6ad4161893ca83bd5bb69478a1ef9f0c&lang=en";
    try {
      let response = await fetch(endpoint);
      if (!response.ok) {
        throw new Error("Something Went Wrong");
      }
      let data = await response.json();
      let loadedFeed = [];
      let newsList = data.articles;
      for (let newsLists of newsList) {
        loadedFeed.push({
          time: newsLists.publishedAt,
          id: newsLists.publishedAt,
          title: newsLists.title,
          content: newsLists.content,
          image: newsLists.image,
          url: newsLists.url,
          source: newsLists.source.name,
        });
        setFeed(loadedFeed);
      }
    } catch (error) {
      setError(error.message);
    }
  }

  // useEffect(() =>{
  //  fetchedNewsHandler()
  // }, [])

  let fetchTest = () => {
    fetchedNewsHandler();
  };
  return (
    <>
      <NavBar />
      <div className={styles.cardCover}>
        <h1 className={styles.heading}>
          World<span className={styles.span}>News</span>
        </h1>
        <ul className={styles.list}>
          {feed.map((feeds) => (
            <div className={styles.card} key={feeds.id}>
              <div className={styles.wrapper}>
                <img src={feeds.image} />
              </div>
              <h1 className={styles.title}>{feeds.title}</h1>
              <h3 className={styles.content}>{feeds.content}</h3>
              <h3 className={styles.time}>
                <a href={feeds.url} target="_blank">
                  Read More
                </a>
              </h3>
              <h3 className={styles.time}>Source: {feeds.source}</h3>
              <p className={styles.time}>{feeds.time}</p>
            </div>
          ))}
        </ul>
      </div>
      <button onClick={fetchTest}>Click me</button>
    </>
  );
}

export default RandomNewsFeed;
