import React, { useState } from 'react'
import './ExpenseForm.css'

let ExpenseForm = () => {
    let [enteredTitle, setEnteredTitle] = useState('')
    let [enteredAmount, setEnteredAmount] = useState('')


    let amountChanheHandler = (e) => {
        console.log(e.target.value)
    }
    let titleChangeHandler = (e) => {
        setEnteredTitle(e.target.value)
    }
    let dateChangeHandler = (e) =>{
        console.log(e.target.value)
    }
    return (
        <div>
            <form>
                <div className='new-expense__controls'>
                    <div className='new-expense__control'>
                        <label>Title</label>
                        <input type='text' onChange={titleChangeHandler}></input>
                    </div>
                    <div className='new-expense__control'>
                        <label>Amount</label>
                        <input type='number' min='0.01' step='0.01' onChange={amountChanheHandler}></input>
                    </div>
                    <div className='new-expense__control'>
                        <label>Date</label>
                        <input type='date' min='2019-01-01' max='2023-12-31' onChange={dateChangeHandler}></input>
                    </div>
                    <div className='new-expense__actions'>
                        <button type='submit'>Add Expense</button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default ExpenseForm
