import React from "react";
import styles from "./RandomNewsFeed.module.css";

function RandomNewsFeed(props) {
  return (
    <div>
      <h1 className={styles.heading}>World news</h1>
      <ul className={styles.list}>
        {props.feed.map((feeds) => (
          <div className={styles.cardCover}>
            <div className={styles.card} key={feeds.id}>
              <h1>{feeds.title}</h1>
              <p>{feeds.time}</p>
              <h3>{feeds.content}</h3>
              <div className={styles.wrapper}>
                <img src={feeds.image} />
              </div>
              <h3>
                <a href={feeds.url} target="_blank">
                  Read More
                </a>
              </h3>
              <h3>{feeds.source}</h3>
            </div>
          </div>
        ))}
      </ul>
    </div>
  );
}

export default RandomNewsFeed;
