import React, { useState } from "react";
import ExpenseItem from './ExpenseItem';
import './Expense.css'
import Card from '../UI/Card';
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";


function Expense(props) {
    let [yearEntered, setYearEntered] = useState('2020')
    let filterChangeHandler = selectedYear => {
        return setYearEntered(selectedYear)

    }
    let filteredYear = props.user.filter(expense => {
        return expense.date.getFullYear().toString() === yearEntered;
    })
    let expenseContent = <p>No expenses found.</p>
    if(filteredYear.length > 0){
       expenseContent = filteredYear.map((expense) => (
            <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}

            />
        )
        )}
    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter year={yearEntered} onChangeYear={filterChangeHandler} />
                {/* <ExpensesFilter /> */}
                {expenseContent}
            </Card>
        </div>
    )
}

export default Expense
