import React, { useState, useEffect } from 'react';
import styles from './App.module.css'
import NavBar from './Components/NavBar';
import Footer from './Components/Footer'
import RandomNewsFeed from './RandomNewsFeed';



const App = () => {
  let [feed, setFeed] = useState('')
  let [error, setError] = useState(null)

  async function fetchedNewsHandler() {
    let endpoint = 'https://gnews.io/api/v4/top-headlines?token=6ad4161893ca83bd5bb69478a1ef9f0c&lang=en'
    try {
      let response = await fetch(endpoint)
      if (!response.ok) {
        throw new Error('Something Went Wrong')
      }
      let data = await response.json()
      console.log(data)
      for (let i = 0; i < 11; i++) {
        // console.log(data.articles[i].title)
        let newsFeed = data.articles[i].map((news) => {
          return {
            time: news.publishedAt,
            id: news.publishedAt , 
            title: news.title,
            content: news.content,
            image: news.image,
            url: news.url,
            source: news.source.name
          }
        })
        setFeed(newsFeed)
      }



    }
    catch (error) {
      setError(error.message)
    }
  }

  // useEffect(() =>{
  //  fetchedNewsHandler()
  // }, [])

  let fetchTest = () => {
    fetchedNewsHandler()
  }

  return (
    <div>
      <NavBar />
      <RandomNewsFeed />
      <button onClick={fetchTest}>Click me</button>
    <h1>{feed}</h1>
      {/* Don't forget to come here after!!! */}
      {/* <Footer /> */}
    </div>
  );
};

export default App;
