import React, { useState } from "react";
import './NewExpense.css'
import ExpenseForm from './ExpenseForm'


let NewExpense = (props) => {
    let saveExpenseDataHandler = (enteredExpenseData) => {
        let expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        props.onAddExpense(expenseData)
        setAdd(false)
    }
    let [add, setAdd] = useState(false)
    let AddHandler = () => {
        setAdd(true)
    }
    function cancelHandler() {
        setAdd(false)
    }

    return (
        <div className="new-expense">
           return !add && <button onClick={AddHandler}>Add New Expense</button>
            {add && <ExpenseForm remove={cancelHandler} onSaveExpenseData={saveExpenseDataHandler} />}
        </div>
    )
}

export default NewExpense
