import React, { useState } from 'react'
import './ExpenseForm.css'

let ExpenseForm = (props) => {
    let [enteredTitle, setEnteredTitle] = useState('')
    let [enteredAmount, setEnteredAmount] = useState('')
    let [enteredDate, setEnteredDate] = useState('')


    let amountChanheHandler = (e) => {
        setEnteredAmount(e.target.value)
    }
    let titleChangeHandler = (e) => {
        setEnteredTitle(e.target.value)
    }
    let dateChangeHandler = (e) => {
        setEnteredDate(e.target.value)
    }
    let submitHandler = (e) => {
        e.preventDefault()

        let expenseData = {
            title: enteredTitle,
            amount: enteredAmount,
            date: new Date(enteredDate)
        }
        props.onSaveExpenseData()
        setEnteredTitle('')
        setEnteredAmount('')
        setEnteredDate('')
    }
    return (
        <div>
            <form onClick={submitHandler}>
                <div className='new-expense__controls'>
                    <div className='new-expense__control'>
                        <label>Title</label>
                        <input
                            type='text'
                            value={enteredTitle}
                            onChange={titleChangeHandler}></input>
                    </div>
                    <div className='new-expense__control'>
                        <label>Amount</label>
                        <input
                            value={enteredAmount}
                            type='number'
                            min='0.01'
                            step='0.01'
                            onChange={amountChanheHandler}></input>
                    </div>
                    <div className='new-expense__control'>
                        <label>Date</label>
                        <input
                            value={enteredDate}
                            type='date'
                            min='2019-01-01'
                            max='2023-12-31'
                            onChange={dateChangeHandler}></input>
                    </div>
                    <div className='new-expense__actions'>
                        <button type='submit'>Add Expense</button>
                    </div>
                </div>
            </form>
        </div>
    )
}

export default ExpenseForm
