import React, {useState} from "react";
import ExpenseItem from './ExpenseItem';
import './Expense.css'
import Card from '../UI/Card';
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";


function Expense(props) {
    let [yearEntered, setYearEntered] =useState('')
    let filterChangeHandler = selectedYear => {
        setYearEntered(selectedYear)
    }
    return (
        <div>
            <Card className='expenses'>
                <ExpensesFilter year = {yearEntered} onChangeYear={filterChangeHandler} />
                <ExpenseItem
                    title={props.user[0].title}
                    amount={props.user[0].amount}
                    date={props.user[0].date}></ExpenseItem>
                <ExpenseItem
                    title={props.user[1].title}
                    amount={props.user[1].amount}
                    date={props.user[1].date}></ExpenseItem>
                <ExpenseItem
                    title={props.user[2].title}
                    amount={props.user[2].amount}
                    date={props.user[2].date}></ExpenseItem>
                <ExpenseItem
                    title={props.user[3].title}
                    amount={props.user[3].amount}
                    date={props.user[3].date}></ExpenseItem>
            </Card>
        </div>
    )
}

export default Expense
